// Focus an input field on page load using useRef hook

import React from 'react'

function focusInput(){
    const inputRef = useRef(null);//bow will be starting from nothing(emppty)

    const handleFocus =()=>{
        inputRef.current.focus();//tell the input to wake up 
    }
    return(
        <div>
            <input ref={inputRef} placeholder="Enter your name"/>
            <button onClick={handleFocus}>Focus the input</button>
        </div>
    )
}


export default Example_1
