// Custom Hook to increment and decrement the count value

import React, { useState } from 'react'

//Custom Hook

function useCounter(startingValue=0){
    const [count,setCount]=useState(startingValue);//Initial value which is 0

    const increment = () => {
        setCount(prev => prev + 1); // Correct way to update state
    };

    const decrement = () => {
        setCount(prev => prev - 1);
    };


    return {count , increment , decrement}
}


// Now we gonna use it in the Component
function Counter() { // Renamed to uppercase
    const { count, increment, decrement } = useCounter(0);

    return (
        <div>
            <p>Count: {count}</p>
            <button onClick={increment}>Add 1</button> {/* Fixed onClick */}
            <button onClick={decrement}>Sub 1</button>  {/* Fixed onClick */}
        </div>
    );
}

export default Counter;
