//Click Tracker

import React, { useRef } from 'react'

function ClickCounter(){
    const clickCount=useRef(0);//Initial value is zero 

    const handleClick=()=>{
        clickCount.current=clickCount.current+1;//This will increase the value of clickCount by 1
    };
    return(
        <div>
            <button onClick={handleClick}>Click Me</button>
            <p>U have click {clickCount.current} times</p>
        </div>
    )
}



export default ClickCounter
